// Hamburger:
window.addEventListener("DOMContentLoaded", () => {
  const e = document.querySelector(".menu"),
    t = document.querySelectorAll(".menu_item"),
    c = document.querySelector(".hamburger");
  const body = document.body;

  c.addEventListener("click", () => {
    c.classList.toggle("hamburger_active"), e.classList.toggle("menu_active");
    body.classList.toggle("no-scroll");
  }),
    t.forEach((t) => {
      t.addEventListener("click", () => {
        c.classList.toggle("hamburger_active"),
          e.classList.toggle("menu_active");
        body.classList.remove("no-scroll");
      });
    });
});
// Top Scroll
let scrollToTopBtn = document.querySelector(".pageup");
// When the user scrolls down 900px from the top of the document, show the button
window.onscroll = function () {
  scrollToTopBtn.style.display =
    document.body.scrollTop > 900 || document.documentElement.scrollTop > 900
      ? "block"
      : "none";
};
// Smooth scrolling
scrollToTopBtn.addEventListener("click", function () {
  window.scrollTo({
    top: 0,
    behavior: "smooth",
  });
});

// Reveal sections
const allSections = document.querySelectorAll(".section");

const revealSection = function (entries, observer) {
  const [entry] = entries;

  if (!entry.isIntersecting) return;

  entry.target.classList.remove("section--hidden");
  observer.unobserve(entry.target);
};

const sectionObserver = new IntersectionObserver(revealSection, {
  root: null,
  threshold: 0.15,
});

allSections.forEach(function (section) {
  sectionObserver.observe(section);
  section.classList.add("section--hidden");
});
